mapper.select:
SELECT u.user_id,
       RANK() OVER (ORDER BY u.user_salary DESC) AS user_rank
FROM user u;

SELECT user_login_type
FROM user
WHERE user_id = #{userId};

mapper.update:
update user
set user_password = #{userPassword},
    user_intro = #{userIntro},
    user_nickname = #{userNickname}
where user_id = #{userId};

